import React, { useState } from "react";
import { authAPI, fileAPI } from "../services/api";
//import PopupMessage from "./PopupMessage";
import ConfirmPopup from "./ConfirmPopup";
import "../styles/Modal.css";

const ShareModal = ({ file, onClose, onSuccess }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [searching, setSearching] = useState(false);
  const [sharing, setSharing] = useState(false);
  const [sharedUsers, setSharedUsers] = useState(file.sharedWith || []);
  const [popup, setPopup] = useState({ show: false, message: "", type: "" });
  const [showConfirm, setShowConfirm] = useState(false);
  const [confirmUserId, setConfirmUserId] = useState(null);

  const showPopup = (message, type = "success") => {
    setPopup({ show: true, message, type });
  };

  // üîç Search for users
  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (query.length < 2) {
      setSearchResults([]);
      return;
    }

    setSearching(true);
    try {
      const response = await authAPI.searchUsers(query);
      setSearchResults(response.data.data.users);
    } catch (err) {
      showPopup("‚ö†Ô∏è Failed to search users", "error");
    } finally {
      setSearching(false);
    }
  };

  // üì§ Share file
  const handleShare = async (user) => {
    setSharing(true);
    try {
      await fileAPI.shareFile(file._id, { userId: user._id });
      showPopup(`‚úÖ File shared with ${user.username}`, "success");
      setSearchQuery("");
      setSearchResults([]);
      onSuccess && onSuccess();
    } catch (err) {
      showPopup(err.response?.data?.message || "‚ùå Failed to share file", "error");
    } finally {
      setSharing(false);
    }
  };

  // üß© Step 1: Trigger confirmation popup before unsharing
  const handleUnshare = (userId) => {
    setConfirmUserId(userId);
    setShowConfirm(true);
  };

  // üß© Step 2: Confirm removal after pressing ‚ÄúConfirm‚Äù in popup
  const confirmUnshare = async () => {
    try {
      await fileAPI.unshareFile(file._id, confirmUserId);
      showPopup("‚úÖ Access removed successfully", "success");

      // ‚úÖ Instantly update local shared users list
      setSharedUsers((prev) =>
        prev.filter((share) => share.user._id !== confirmUserId)
      );

      // ‚úÖ Close confirmation popup
      setShowConfirm(false);
      setConfirmUserId(null);

      // ‚úÖ Refresh dashboard list
      onSuccess && onSuccess();
    } catch (err) {
      showPopup("‚ö†Ô∏è Failed to remove access", "error");
      setShowConfirm(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content large" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Share "{file.originalName}"</h2>
          <button className="close-btn" onClick={onClose}>
            √ó
          </button>
        </div>

        <div className="modal-body">
          {/* üîç Search Users */}
          <div className="search-section">
            <h3>Share with user</h3>
            <input
              type="text"
              placeholder="Search by username or email..."
              value={searchQuery}
              onChange={(e) => handleSearch(e.target.value)}
              className="search-input"
            />

            {searching && <p className="searching">Searching...</p>}

            {searchResults.length > 0 && (
              <div className="search-results">
                {searchResults.map((user) => (
                  <div key={user._id} className="search-result-item">
                    <div className="user-info">
                      <span className="user-icon">üë§</span>
                      <div>
                        <p className="username">{user.username}</p>
                        <p className="email">{user.email}</p>
                      </div>
                    </div>
                    <button
                      onClick={() => handleShare(user)}
                      disabled={sharing}
                      className="btn-primary btn-small"
                    >
                      Share
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* üë• Currently Shared With */}
          <div className="shared-users-section">
            <h3>Currently shared with</h3>
            {sharedUsers.length === 0 ? (
              <p className="no-shares">Not shared with anyone yet</p>
            ) : (
              <div className="shared-users-list">
                {sharedUsers.map((share) => (
                  <div key={share.user._id} className="shared-user-item">
                    <div className="user-info">
                      <span className="user-icon">üë§</span>
                      <div>
                        <p className="username">{share.user.username}</p>
                        <p className="email">{share.user.email}</p>
                        <p className="shared-date">
                          Shared on{" "}
                          {new Date(share.sharedAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                    <button
                      onClick={() => handleUnshare(share.user._id)}
                      className="btn-danger btn-small"
                    >
                      Remove
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="modal-footer">
          <button onClick={onClose} className="btn-secondary">
            Close
          </button>
        </div>
      </div>

      {/* ‚úÖ Styled Success/Error Popup */}
      {popup.show && (
        <PopupMessage
          message={popup.message}
          type={popup.type}
          onClose={() => setPopup({ show: false, message: "", type: "" })}
        />
      )}

      {/* ‚úÖ Themed Confirmation Popup */}
      {showConfirm && (
        <ConfirmPopup
          message="Are you sure you want to remove this user's access?"
          onConfirm={confirmUnshare}
          onCancel={() => setShowConfirm(false)}
        />
      )}
    </div>
  );
};

export default ShareModal;
