import React from "react";
import "../styles/Modal.css"; // Keep your modal animation & overlay styles

const ConfirmPopup = ({ message, onConfirm, onCancel }) => {
  return (
    <div className="modal-overlay flex items-center justify-center">
      <div className="confirm-popup bg-white rounded-2xl shadow-2xl w-[90%] max-w-sm text-center p-6 border border-gray-200">
        {/* ⚠️ Header Message */}
        <p className="confirm-message text-gray-800 font-semibold text-lg mb-5">
          {message}
        </p>

        {/* ✅ Buttons */}
        <div className="flex justify-center gap-4">
          <button
            onClick={onConfirm}
            className="bg-green-600 hover:bg-green-700 text-white px-5 py-2 rounded-lg shadow-md transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-green-400"
          >
            ✅ Confirm
          </button>

          <button
            onClick={onCancel}
            className="bg-red-600 hover:bg-red-700 text-white px-5 py-2 rounded-lg shadow-md transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-red-400"
          >
            ✖ Cancel
          </button>
        </div>

        {/* Decorative line */}
        <div className="mt-6 border-t border-gray-200"></div>
        <p className="text-xs text-gray-500 mt-3 italic">
          Action cannot be undone.
        </p>
      </div>
    </div>
  );
};

export default ConfirmPopup;
