import React from 'react';
import '../styles/FileList.css';

const FileList = ({ 
  files, 
  isOwner, 
  onDownload, 
  onDelete, 
  onShare, 
  onViewAuditLogs,
  formatFileSize,
  formatDate 
}) => {

  // ‚úÖ Simplified Integrity Verification Function
  const verifyIntegrity = async (file) => {
    try {
      const response = await fetch(`http://localhost:5000/api/files/${file._id}/download`, {
        method: 'GET',
        credentials: 'include'
      });

      // ‚ùå If backend rejects (integrity fail or other issue)
      if (!response.ok) {
        const error = await response.json();
        alert(`‚ö†Ô∏è ${error.message || 'Integrity check failed for ' + file.originalName}`);
        return;
      }

      // ‚úÖ Backend adds X-File-Integrity header after verification
      const integrityStatus = response.headers.get('X-File-Integrity');

      if (integrityStatus && integrityStatus !== 'Verified') {
        alert(`‚ö†Ô∏è Integrity mismatch detected for ${file.originalName}`);
      } else {
        alert(`‚úÖ Integrity verified for ${file.originalName}`);
      }

    } catch (error) {
      console.error('Integrity check error:', error);
      alert('Error verifying file integrity.');
    }
  };

  if (files.length === 0) {
    return (
      <div className="empty-state">
        <span className="empty-icon">üìÇ</span>
        <p>No files to display</p>
        {isOwner && <p className="empty-hint">Upload your first file to get started</p>}
      </div>
    );
  }

  return (
    <div className="file-list">
      <table className="file-table">
        <thead>
          <tr>
            <th>File Name</th>
            <th>Size</th>
            <th>Owner</th>
            <th>Uploaded</th>
            {isOwner && <th>Shared With</th>}
            <th>Integrity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {files.map((file) => (
            <tr key={file._id}>
              <td>
                <div className="file-name-cell">
                  <span className="file-icon">üìÑ</span>
                  {file.originalName}
                </div>
              </td>
              <td>{formatFileSize(file.size)}</td>
              <td>{file.owner.username}</td>
              <td>{formatDate(file.uploadedAt)}</td>

              {isOwner && (
                <td>
                  {file.sharedWith.length > 0 ? (
                    <span className="shared-count">{file.sharedWith.length} user(s)</span>
                  ) : (
                    <span className="not-shared">Not shared</span>
                  )}
                </td>
              )}

              {/* üß© Integrity Column */}
              <td>
                <div className="integrity-cell">
                  <code>{file.integrityHash?.slice(0, 10)}...</code>
                  <button
                    onClick={() => verifyIntegrity(file)}
                    className="btn-action btn-verify"
                    title="Verify Integrity"
                  >
                    ‚úÖ
                  </button>
                </div>
              </td>

              <td>
                <div className="action-buttons">
                  <button
                    onClick={() => onDownload(file)}
                    className="btn-action btn-download"
                    title="Download"
                  >
                    ‚¨áÔ∏è
                  </button>

                  {isOwner && (
                    <>
                      <button
                        onClick={() => onShare(file)}
                        className="btn-action btn-share"
                        title="Share"
                      >
                        üë•
                      </button>
                      <button
                        onClick={() => onViewAuditLogs(file)}
                        className="btn-action btn-logs"
                        title="View Audit Logs"
                      >
                        üìã
                      </button>
                      <button
                        onClick={() => onDelete(file._id)}
                        className="btn-action btn-delete"
                        title="Delete"
                      >
                        üóëÔ∏è
                      </button>
                    </>
                  )}
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FileList;
